import React, { Component } from "react";
import SimpleStorageContract from "./contracts/SimpleStorage.json";
import VotingContract from "./contracts/Voting.json";
import getWeb3 from "./getWeb3";

import "./App.css";

class App extends Component {
  state = { status: 0, web3: null, accounts: null, contract: null, proposals: null };


  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = VotingContract.networks[networkId];
      const instance = new web3.eth.Contract(
        VotingContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      // getting the events  
      let options = {
        fromBlock: 0,                  //Number || "earliest" || "pending" || "latest"
        toBlock: 'latest'
      };
      const proposalsEvt = await instance.getPastEvents('ProposalRegistered', options);

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance, proposals: proposalsEvt }, this.runExample);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    // await contract.methods.set(10).send({ from: accounts[0] });

    // Get the value from the contract to prove it worked.
    // const response = await contract.methods.get().call();

    const response = await contract.methods.workflowStatus().call();
    // Update state with the result.
    this.setState({ status: response });

    //await contract.methods.endProposalsRegistering().send({ from: accounts[0] });

    // getting the events  
    let options = {
      fromBlock: 0,                  //Number || "earliest" || "pending" || "latest"
      toBlock: 'latest'
    };
    const proposalsEvt = await contract.getPastEvents('ProposalRegistered', options);

    this.setState({ proposals:proposalsEvt });

  };

  // Ajouter un Votant
  addVoter = async () => {
    const { accounts, contract} = this.state;
    let voterAdress = document.getElementById("voterAdress").value;
    await contract.methods.addVoter(voterAdress).send({ from: accounts[0] });
  };

  // Ajouter une propositions
  addProposal = async () => {
    const { accounts, contract} = this.state;
    let proposalDescrition = document.getElementById("proposalDescrition").value;
    await contract.methods.addProposal(proposalDescrition).send({ from: accounts[0] });
  };

  // Ajouter une propositions
  setVote = async () => {
      const { accounts, contract} = this.state;
      //FIXME get the proposal checkedId
      let proposalId = 1;
      await contract.methods.setVote(proposalId).send({ from: accounts[0] });
  };

  // Comptabiliser les votes
  tallyVotes = async () => {
      const { accounts, contract} = this.state;
      await contract.methods.tallyVotes().send({ from: accounts[0] });
  };

  // Comptabiliser les votes
  getWinner = async () => {
      const { accounts, contract} = this.state;
      const winnerId = await contract.methods.winningProposalID().send({ from: accounts[0] });
      console.log(winnerId);
      document.getElementById("winnerId").value = winnerId;
  };


  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <h1>Good to Go!</h1>
        <p>Your Truffle Box is installed and ready.</p>
        <h2>Smart Contract Example</h2>

        <p>adresse: {this.state.accounts[0]}</p>

        <div>WorkflowStatus: {this.state.status}</div>

        <div>
          <h3>Ajouter un votant</h3>
          Entrer l'adresse du votant<input type="text" id="voterAdress" />
          <button onClick={this.addVoter}>Valider</button>
        </div>

        <div>
          <h3>Enregistrer une Proposition</h3>
          Entrer la Proposition<input type="text" id="proposalDescrition" />
          <button onClick={this.addProposal}>Valider</button>
        </div>

        <div>
          <h3>Voter une proposition</h3>
          {
          this.state.proposals == null ? <p>Pas de propositions</p> : 
            <ul>
              { this.state.proposals.map( (proposal, index) => (
                <div key={index}>
                  <span>{'#' + index + '  ' + proposal.returnValues.proposalDescription}</span>

                  <input value={index} type="checkbox" />
                </div>
               ) ) }
            </ul>
         }
          <button onClick={this.setVote}>Voter</button>
        </div>

        <div>
          <h3>Comptabiliser les votes</h3>

          <button onClick={this.tallyVotes}>Comptabiliser</button>
        </div>

        <div>
          <h3>RÃ©sultat des votes</h3>

          <button onClick={this.getWinner}>Afficher le ganant</button>
          <div><span>Le gagnant est: </span><span id="winnerId"></span></div>
        </div>

        {
          this.state.proposals == null ? <p>Pas de propositions</p> : 
            <ul>
              { this.state.proposals.map( (proposal) => ( <li>{proposal.returnValues.proposalDescription}</li> ) ) }
            </ul>
        }

      </div>
    );
  }
}

export default App;
